"use client";

import { useActionState, useState, useEffect } from "react";
import { login } from "@/app/actions/auth";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import Link from "next/link";
import { Loader2, Eye, EyeOff } from "lucide-react";
import { toast } from "sonner";
import { useRouter } from "next/navigation";

type ActionState = {
  message?: string;
  error?: {
    email?: string[];
    password?: string[];
  };
};

const initialState: ActionState = {
  message: "",
  error: undefined,
};

export function LoginForm() {
  const [state, formAction, isPending] = useActionState(login, initialState);
  const [showPassword, setShowPassword] = useState(false);
  const router = useRouter();

  useEffect(() => {
    if (state?.message) {
      toast.error(state.message);
    }
  }, [state]);

  return (
    <div className="space-y-6 animate-fade-in">
      <div className="space-y-2 text-center lg:text-left">
        <h2 className="text-3xl font-bold font-heading tracking-tight">Masuk Akun</h2>
        <p className="text-muted-foreground">
          Masukkan email dan password untuk mengakses dashboard.
        </p>
      </div>

      <form action={formAction} className="space-y-4">
        <div className="space-y-2">
          <Label htmlFor="email">Email atau Username</Label>
          <Input
            id="email"
            name="email"
            type="text"
            placeholder="Email atau Username"
            required
            className="h-11 bg-background/50 backdrop-blur-sm"
          />
          {state?.error?.email && (
            <p className="text-sm text-destructive">{state.error.email}</p>
          )}
        </div>

        <div className="space-y-2">
          <div className="flex items-center justify-between">
            <Label htmlFor="password">Password</Label>
          </div>
          <div className="relative">
            <Input
              id="password"
              name="password"
              type={showPassword ? "text" : "password"}
              placeholder=""
              required
              className="h-11 bg-background/50 backdrop-blur-sm pr-10"
            />
            <button
              type="button"
              onClick={() => setShowPassword(!showPassword)}
              className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
            >
              {showPassword ? (
                <EyeOff className="h-4 w-4" />
              ) : (
                <Eye className="h-4 w-4" />
              )}
            </button>
          </div>
          {state?.error?.password && (
            <p className="text-sm text-destructive">{state.error.password}</p>
          )}
        </div>

        <Button
          type="submit"
          className="w-full h-11 text-base font-medium btn-shine mt-6"
          disabled={isPending}
        >
          {isPending ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              Memproses...
            </>
          ) : (
            "Masuk Sekarang"
          )}
        </Button>
      </form>

      <div className="text-center text-sm text-muted-foreground">
        Belum punya akun?{" "}
        <Link
          href="/auth/register"
          className="font-medium text-primary hover:text-primary/80 underline-offset-4 hover:underline transition-all"
        >
          Daftar disini
        </Link>
      </div>
    </div>
  );
}

"use client";

import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  Calendar, 
  MapPin, 
  Clock, 
  ArrowRight, 
  Mic, 
  Trophy, 
  Heart, 
  ChevronLeft, 
  ChevronRight 
} from "lucide-react";
import Link from "next/link";
import { useState, useEffect } from "react";
import {
  Carousel,
  CarouselContent,
  CarouselItem,
} from "@/components/ui/carousel";

import { EventItem, EventCategory } from "@/types";
import { CarouselApi } from "@/components/ui/carousel";

const categoryIcons: Record<EventCategory, React.ReactNode> = {
  kajian: <Mic className="w-6 h-6 text-white" />,
  olahraga: <Trophy className="w-6 h-6 text-white" />,
  sosial: <Heart className="w-6 h-6 text-white" />,
  lainnya: <Calendar className="w-6 h-6 text-white" />,
};

const categoryGradients: Record<EventCategory, string> = {
  kajian: "bg-gradient-to-br from-emerald-400 to-emerald-600",
  olahraga: "bg-gradient-to-br from-blue-400 to-blue-600",
  sosial: "bg-gradient-to-br from-rose-400 to-rose-600",
  lainnya: "bg-gradient-to-br from-amber-400 to-amber-600",
};

function AgendaCard({ event }: { event: EventItem }) {
  const dateObj = new Date(event.date);
  const dayName = dateObj.toLocaleDateString("id-ID", { weekday: "long" });
  const dateStr = dateObj.toLocaleDateString("id-ID", { day: "numeric", month: "long" });

  // Calculate status
  const now = new Date();
  // Reset time part for accurate date comparison if needed, but requirements say "Tanggal kegiatan sudah lewat dari tanggal hari ini"
  // Let's keep it simple: strict comparison of dates could arguably look at time too, but usually strict date comparison assumes end of day.
  // Requirement: "Tanggal kegiatan sudah lewat dari tanggal hari ini" -> "Sudah Selesai"
  // Requirement: "Tanggal kegiatan sama dengan hari ini atau masih akan datang" -> "Akan Datang"
  
  // Set comparison to beginning of day to be safe, or just compare direct timestamps if date string is YYYY-MM-DD
  const eventDate = new Date(event.date); // Assuming YYYY-MM-DD or ISO
  eventDate.setHours(0,0,0,0);
  const today = new Date();
  today.setHours(0,0,0,0);

  const isFinished = eventDate < today;
  
  return (
    <div className="group relative bg-white dark:bg-slate-800 rounded-[2rem] p-8 shadow-sm hover:shadow-xl transition-all duration-300 h-full border border-slate-100 dark:border-slate-700 flex flex-col justify-between">
      
      {/* Top Section: Icon & Category */}
      <div>
        <div className="flex justify-between items-start mb-6">
          <div className={`w-14 h-14 rounded-2xl ${categoryGradients[event.category]} flex items-center justify-center shadow-lg transform group-hover:scale-110 transition-transform duration-300`}>
            {categoryIcons[event.category]}
          </div>
           {/* Status Label */}
           <Badge 
            variant={isFinished ? "secondary" : "default"}
            className={`${
              isFinished 
                ? "bg-slate-100 text-slate-500 hover:bg-slate-200" 
                : "bg-emerald-50 text-emerald-600 hover:bg-emerald-100 border-emerald-100"
              } border border-transparent shadow-none px-3 py-1 text-xs font-bold uppercase tracking-wider`}
          >
            {isFinished ? "Sudah Selesai" : "Akan Datang"}
          </Badge>
        </div>

        <h3 className="text-2xl font-bold mb-2 group-hover:text-primary transition-colors line-clamp-2">
          {event.title}
        </h3>
        
        <div className="flex items-center gap-2 text-sm text-slate-600 dark:text-slate-400 mb-4 font-medium">
          <Calendar className="w-4 h-4" />
          <span>{dayName}, {dateStr}</span>
          <span className="w-1 h-1 rounded-full bg-slate-300 mx-1" />
          <Clock className="w-4 h-4" />
          <span>{event.time}</span>
        </div>

        <div 
          className="text-slate-600 dark:text-slate-300 text-sm leading-relaxed mb-6 line-clamp-3 prose prose-sm prose-emerald"
          dangerouslySetInnerHTML={{ __html: event.description }}
        />
      </div>

      {/* Button Action */}
      <div className="flex items-center justify-between mt-auto">
         <div className="flex items-center gap-1 text-xs font-bold uppercase tracking-wider text-slate-600">
            <MapPin className="w-3 h-3" />
            {event.location}
         </div>
         
         <Link href={`/agenda/${event.id}`}>
           <button aria-label={`Detail event ${event.title}`} className="w-10 h-10 rounded-full bg-slate-900 dark:bg-white text-white dark:text-slate-900 flex items-center justify-center hover:scale-110 transition-transform shadow-lg">
             <ArrowRight className="w-5 h-5" />
           </button>
         </Link>
      </div>
    </div>
  );
}

export function AgendaSection({ events = [] }: { events?: EventItem[] }) {
  const [api, setApi] = useState<CarouselApi>()
  const [current, setCurrent] = useState(0)
  const [count, setCount] = useState(0)

  useEffect(() => {
    if (!api) {
      return
    }
 
    setCount(api.scrollSnapList().length)
    setCurrent(api.selectedScrollSnap() + 1)
 
    api.on("select", () => {
      setCurrent(api.selectedScrollSnap() + 1)
    })
  }, [api])

  return (
    <section id="agenda" className="section-padding bg-slate-50/50 dark:bg-slate-900/50 relative">
      <div className="container-custom">
        <div className="flex flex-col md:flex-row items-start md:items-end justify-between mb-8 md:mb-12 gap-6">
          <div className="max-w-xl">
             <div className="inline-flex items-center gap-2 mb-3 px-3 py-1 rounded-full bg-emerald-50 text-emerald-600 border border-emerald-100 w-fit">
                <Calendar className="w-3.5 h-3.5" />
                <span className="text-xs font-bold tracking-widest uppercase">AGENDA MASJID</span>
             </div>
            <h2 className="text-4xl md:text-5xl font-bold tracking-tight text-slate-900 dark:text-white">
              Kegiatan Terdekat
            </h2>
          </div>
          
          {/* Custom Header Buttons - Only show if there are events */}
          {events.length > 0 && (
            <div className="flex gap-2 relative z-10 self-end md:self-auto">
               <Button 
                 onClick={() => api?.scrollPrev()} 
                 variant="outline" 
                 size="icon" 
                 aria-label="Previous agenda"
                 className="h-12 w-12 rounded-full border-slate-200 bg-white hover:bg-slate-50 hover:text-primary transition-all shadow-sm"
               >
                 <ChevronLeft className="w-6 h-6" />
               </Button>
               <Button 
                 onClick={() => api?.scrollNext()} 
                 variant="outline" 
                 size="icon" 
                 aria-label="Next agenda"
                 className="h-12 w-12 rounded-full border-slate-200 bg-white hover:bg-slate-50 hover:text-primary transition-all shadow-sm"
               >
                 <ChevronRight className="w-6 h-6" />
               </Button>
            </div>
          )}
        </div>

        {/* Content */}
        <div className="relative">
          {events.length > 0 ? (
            <Carousel
              setApi={setApi}
              opts={{
                align: "start",
                loop: true,
              }}
              className="w-full"
            >
              <CarouselContent className="-ml-4 pb-4">
                {events.map((event) => (
                  <CarouselItem key={event.id} className="pl-4 md:basis-1/2 lg:basis-1/3">
                    <div className="h-full">
                      <AgendaCard event={event} />
                    </div>
                  </CarouselItem>
                ))}
              </CarouselContent>
            </Carousel>
          ) : (
            // Empty State
            <div className="text-center py-20 bg-white dark:bg-slate-800 rounded-[2rem] border border-dashed border-slate-200 dark:border-slate-700">
              <div className="flex flex-col items-center justify-center gap-4">
                <div className="w-16 h-16 rounded-full bg-slate-50 dark:bg-slate-700 flex items-center justify-center">
                   <Calendar className="w-8 h-8 text-slate-400" />
                </div>
                <p className="text-slate-500 dark:text-slate-400 font-medium">Belum ada agenda terdekat</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </section>
  );
}

"use client";

import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ArrowRight, Calendar, User, BookOpen, ChevronLeft, ChevronRight } from "lucide-react";
import Link from "next/link";
import Image from "next/image";
import { useState, useEffect } from "react";
import {
  Carousel,
  CarouselContent,
  CarouselItem,
} from "@/components/ui/carousel";

import { ArticleItem } from "@/types";
import { CarouselApi } from "@/components/ui/carousel";
import { getArticles } from "@/app/actions/articles";
import { toast } from "sonner";

export function ArticleSection() {
  const [api, setApi] = useState<CarouselApi>()
  const [articles, setArticles] = useState<ArticleItem[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    async function fetchArticles() {
      try {
        const data = await getArticles(6); // Fetch latest 6
        setArticles(data);
      } catch (error) {
        console.error("Failed to fetch articles", error);
        toast.error("Gagal memuat artikel terbaru");
      } finally {
        setIsLoading(false);
      }
    }
    fetchArticles();
  }, []);

  return (
    <section id="artikel" className="section-padding bg-white relative">
      <div className="container-custom">
        <div className="flex flex-col md:flex-row items-start md:items-end justify-between mb-8 md:mb-12 gap-6">
          <div className="max-w-xl">
             <div className="inline-flex items-center gap-2 mb-3 px-3 py-1 rounded-full bg-emerald-50 text-emerald-600 border border-emerald-100 w-fit">
               <BookOpen className="w-3.5 h-3.5" />
               <span className="text-xs font-bold tracking-widest uppercase">Wawasan & Literasi</span>
            </div>
            <h2 className="text-4xl md:text-5xl font-bold tracking-tight text-slate-900 mb-4">
              Artikel Terbaru
            </h2>
            <p className="text-slate-500 text-lg">
              Baca artikel-artikel islami, ringkasan kajian, dan berita terbaru dari kegiatan Masjid Jami'.
            </p>
          </div>
          
          {/* Custom Header Buttons */}
          <div className="flex gap-2 relative z-10 self-end md:self-auto">
             <Button 
               onClick={() => api?.scrollPrev()} 
               variant="outline" 
               size="icon" 
               disabled={!articles.length}
               aria-label="Previous article"
               className="h-12 w-12 rounded-full border-slate-200 bg-white hover:bg-slate-50 hover:text-emerald-600 transition-all shadow-sm"
             >
               <ChevronLeft className="w-6 h-6" />
             </Button>
             <Button 
               onClick={() => api?.scrollNext()} 
               variant="outline" 
               size="icon" 
               disabled={!articles.length}
               aria-label="Next article"
               className="h-12 w-12 rounded-full border-slate-200 bg-white hover:bg-slate-50 hover:text-emerald-600 transition-all shadow-sm"
             >
               <ChevronRight className="w-6 h-6" />
             </Button>
          </div>
