"use client";

import { useEditor, EditorContent } from "@tiptap/react";
import { useEffect } from "react";
import StarterKit from "@tiptap/starter-kit";
import Underline from "@tiptap/extension-underline";
import Link from "@tiptap/extension-link";
import { Button } from "@/components/ui/button";
import { Bold, Italic, Underline as UnderlineIcon, List, ListOrdered, Quote, Undo, Redo } from "lucide-react";

interface RichTextEditorProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
}

export function RichTextEditor({ value, onChange, placeholder }: RichTextEditorProps) {
  const editor = useEditor({
    extensions: [
      StarterKit,
      Underline,
      Link.configure({
        openOnClick: false,
      }),
    ],
    content: value,
    immediatelyRender: false, // Fix SSR hydration mismatch
    editorProps: {
      attributes: {
        class: "min-h-[150px] w-full rounded-md border border-slate-200 bg-white px-3 py-2 text-sm ring-offset-white placeholder:text-slate-500 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-emerald-600 focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 prose prose-sm max-w-none dark:border-slate-800 dark:bg-slate-950 dark:ring-offset-slate-950 dark:placeholder:text-slate-400 dark:focus-visible:ring-slate-300",
      },
    },
    onUpdate: ({ editor }) => {
      onChange(editor.getHTML());
    },
  });

  // Sync value changes from parent (e.g. form reset)
  useEffect(() => {
    if (editor && value !== editor.getHTML()) {
      editor.commands.setContent(value);
    }
  }, [value, editor]);

  if (!editor) {
    return null;
  }

  return (
    <div className="space-y-2">
       {/* Toolbar */}
      <div className="flex flex-wrap items-center gap-1 p-1 border border-slate-200 rounded-lg bg-slate-50">
        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => editor.chain().focus().toggleBold().run()}
          className={editor.isActive("bold") ? "bg-slate-200 text-slate-900" : "text-slate-500"}
          title="Bold"
        >
          <Bold className="w-4 h-4" />
        </Button>
        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => editor.chain().focus().toggleItalic().run()}
          className={editor.isActive("italic") ? "bg-slate-200 text-slate-900" : "text-slate-500"}
          title="Italic"
        >
          <Italic className="w-4 h-4" />
        </Button>
        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => editor.chain().focus().toggleUnderline().run()}
          className={editor.isActive("underline") ? "bg-slate-200 text-slate-900" : "text-slate-500"}
          title="Underline"
        >
          <UnderlineIcon className="w-4 h-4" />
        </Button>
        
        <div className="w-px h-6 bg-slate-300 mx-1" />

        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => editor.chain().focus().toggleBulletList().run()}
          className={editor.isActive("bulletList") ? "bg-slate-200 text-slate-900" : "text-slate-500"}
          title="Bullet List"
        >
          <List className="w-4 h-4" />
        </Button>
        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => editor.chain().focus().toggleOrderedList().run()}
          className={editor.isActive("orderedList") ? "bg-slate-200 text-slate-900" : "text-slate-500"}
          title="Ordered List"
        >
          <ListOrdered className="w-4 h-4" />
        </Button>

        <div className="w-px h-6 bg-slate-300 mx-1" />

        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => editor.chain().focus().toggleBlockquote().run()}
          className={editor.isActive("blockquote") ? "bg-slate-200 text-slate-900" : "text-slate-500"}
          title="Blockquote"
        >
          <Quote className="w-4 h-4" />
        </Button>

        <div className="flex-1" />

        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => editor.chain().focus().undo().run()}
          disabled={!editor.can().undo()}
          className="text-slate-500"
          title="Undo"
        >
          <Undo className="w-4 h-4" />
        </Button>
        <Button
          type="button"
          variant="ghost"
          size="sm"
          onClick={() => editor.chain().focus().redo().run()}
          disabled={!editor.can().redo()}
          className="text-slate-500"
          title="Redo"
        >
          <Redo className="w-4 h-4" />
        </Button>
      </div>

      <EditorContent editor={editor} />
    </div>
  );
}

"use client";

import React, { createContext, useContext, useState, useEffect } from "react";
import { EventItem, ArticleItem, GalleryItem } from "@/types";
import { createEvent, updateEvent, deleteEvent as deleteEventAction } from "@/app/actions/events";
import { createArticle, updateArticle, deleteArticle as deleteArticleAction } from "@/app/actions/articles";
import { createGallery, deleteGallery as deleteGalleryAction } from "@/app/actions/gallery";

interface AdminContextType {
  events: EventItem[];
  articles: ArticleItem[];
  galleries: GalleryItem[];
  setEvents: React.Dispatch<React.SetStateAction<EventItem[]>>;
  setArticles: React.Dispatch<React.SetStateAction<ArticleItem[]>>;
  setGalleries: React.Dispatch<React.SetStateAction<GalleryItem[]>>;
  // Helpers
  addEvent: (event: EventItem) => Promise<void>;
  updateEvent: (event: EventItem) => Promise<void>;
  deleteEvent: (id: string) => Promise<void>;
  addArticle: (article: ArticleItem) => Promise<void>;
  updateArticle: (article: ArticleItem) => Promise<void>;
  deleteArticle: (id: string) => Promise<void>;
  updateGallery: (gallery: GalleryItem) => Promise<void>;
  deleteGallery: (id: string) => Promise<void>;
}

const AdminContext = createContext<AdminContextType | undefined>(undefined);

const EMPTY_EVENTS: EventItem[] = [];
const EMPTY_ARTICLES: ArticleItem[] = [];
const EMPTY_GALLERIES: GalleryItem[] = [];

interface AdminProviderProps {
  children: React.ReactNode;
  initialEvents?: EventItem[];
  initialArticles?: ArticleItem[];
  initialGalleries?: GalleryItem[];
}

export function AdminProvider({
  children,
  initialEvents = EMPTY_EVENTS,
  initialArticles = EMPTY_ARTICLES,
  initialGalleries = EMPTY_GALLERIES
}: AdminProviderProps) {
  // Initialize state with props
  const [events, setEvents] = useState<EventItem[]>(initialEvents);
  const [articlesState, setArticles] = useState<ArticleItem[]>(initialArticles);
  const [galleries, setGalleries] = useState<GalleryItem[]>(initialGalleries);

  // Sync state with props when they change (revalidation)
  useEffect(() => {
    setEvents(initialEvents);
  }, [initialEvents]);

  useEffect(() => {
    setArticles(initialArticles);
  }, [initialArticles]);

  useEffect(() => {
    setGalleries(initialGalleries);
  }, [initialGalleries]);

  // Helper functions
  const addEvent = async (event: EventItem) => {
    // Optimistic update
    setEvents((prev) => [event, ...prev]);
    try {
      await createEvent(event);
    } catch (error) {
      console.error("Failed to add event", error);
      // Rollback? For now simple optimistic or just rely on revalidate
    }
  };

  const updateEventFn = async (event: EventItem) => {
    setEvents((prev) => prev.map((e) => (e.id === event.id ? event : e)));
    try {
      await updateEvent(event);
    } catch (error) {
      console.error("Failed to update event", error);
    }
  };

  const deleteEventFn = async (id: string) => {
    setEvents((prev) => prev.filter((e) => e.id !== id));
    try {
      await deleteEventAction(id);
    } catch (error) {
      console.error("Failed to delete event", error);
    }
  };

  const addArticle = async (article: ArticleItem) => {
    setArticles((prev) => [article, ...prev]);
    try {
      await createArticle(article);
    } catch (error) {
      console.error("Failed to add article", error);
    }
  };

  const updateArticleFn = async (article: ArticleItem) => {
    setArticles((prev) => prev.map((a) => (a.id === article.id ? article : a)));
    try {
      await updateArticle(article);
    } catch (error) {
      console.error("Failed to update article", error);
    }
  };

  const deleteArticleFn = async (id: string) => {
    setArticles((prev) => prev.filter((a) => a.id !== id));
    try {
      await deleteArticleAction(id);
    } catch (error) {
      console.error("Failed to delete article", error);
    }
  };

  // Gallery actions - assuming we want to use server actions here too
  const updateGallery = async (gallery: GalleryItem) => {
    // Gallery update might not be implemented in server actions yet, 
    // but keeping signature. If no action, just state.
    setGalleries((prev) => prev.map((g) => (g.id === gallery.id ? gallery : g)));
  };

  const deleteGalleryFn = async (id: string) => {
    setGalleries((prev) => prev.filter((g) => g.id !== id));
    try {
      await deleteGalleryAction(id);
    } catch (error) {
      console.error("Failed to delete gallery", error);
    }
  };

  return (
    <AdminContext.Provider
      value={{
        events,
        articles: articlesState,
        galleries,
        setEvents,
        setArticles,
        setGalleries,
        addEvent,
        updateEvent: updateEventFn,
        deleteEvent: deleteEventFn,
        addArticle,
        updateArticle: updateArticleFn,
        deleteArticle: deleteArticleFn,
        updateGallery,
        deleteGallery: deleteGalleryFn,
      }}
    >
      {children}
    </AdminContext.Provider>
  );
}

export function useAdmin() {
  const context = useContext(AdminContext);
  if (context === undefined) {
    throw new Error("useAdmin must be used within an AdminProvider");
  }
  return context;
}

import { createServerClient } from "@supabase/ssr";
import { NextResponse, type NextRequest } from "next/server";

export async function updateSession(request: NextRequest) {
  let response = NextResponse.next({
    request: {
      headers: request.headers,
    },
  });

  const supabase = createServerClient(
    process.env.NEXT_PUBLIC_SUPABASE_URL!,
    process.env.NEXT_PUBLIC_SUPABASE_PUBLISHABLE_DEFAULT_KEY!,
    {
      cookies: {
        getAll() {
          return request.cookies.getAll();
        },
        setAll(cookiesToSet) {
          cookiesToSet.forEach(({ name, value, options }) =>
            request.cookies.set(name, value)
          );
          response = NextResponse.next({
            request,
          });
          cookiesToSet.forEach(({ name, value, options }) =>
            response.cookies.set(name, value, options)
          );
        },
      },
    }
  );

  const {
    data: { user },
  } = await supabase.auth.getUser();

  // Protected routes
  if (request.nextUrl.pathname.startsWith("/admin") && !user) {
    return NextResponse.redirect(new URL("/auth/login", request.url));
  }

  // Auth routes (redirect to admin if already logged in)
  if (request.nextUrl.pathname.startsWith("/auth") && user) {
    return NextResponse.redirect(new URL("/admin", request.url));
  }

  return response;
}
